trace.source:
PROP_Z0;
if (x > xmin && x < xmax && y > ymin && y < ymax)
{
  int i = floor(fmod(1E6 * t, period) / tick);
  if(i >= 0 && i < n_bins) {
    #pragma acc atomic      
    TOF_N[i] = TOF_N[i] + 1;
    #pragma acc atomic
    TOF_p[i] = TOF_p[i] + p;
    #pragma acc atomic      
    TOF_w[i] = TOF_w[i] + p * p;
    SCATTER;
  }
}
if (restore_neutron) {
  RESTORE_NEUTRON(INDEX_CURRENT_COMP, x, y, z, vx, vy, vz, t, sx, sy, sz, p);
}

