          randderotate(&vx, &vy, &vz, Alpha, Beta, Gamma);
        }
        if (PG) { /* orientation of crystallite is longer random */
          PGderotate(&vx, &vy, &vz, Alpha, hkl_info.csx, hkl_info.csy, hkl_info.csz);
        }

        type = 't';
            if (!itype) itype = 1;
        #ifndef OPENACC
              hkl_info.type = type;
        #endif

